import React from 'react';


class AnalysisPage extends React.Component {
    state = {
        analyze_mode: 'mode1',
        fileName: '',
        fileToUpload: undefined,
    };

    onModeChange = (e) => {
        this.setState({ analyze_mode: e.target.value });
    };

    handleFileUpload = (e) => {
        console.log(e.target.files[0]);
        this.setState({ fileToUpload: e.target.files[0] });
    };

    onUploadButtonClick = (e) => {
    };


    render() {
        return (
            <div className="analyze">
                <div className="radio-group">
                    <div className="radio">
                        <input
                            type="radio"
                            value='mode1'
                            onChange={this.onModeChange}
                            checked={this.state.analyze_mode === 'mode1'}
                        />
                        <span>Статический анализ</span>
                    </div>
                    <div className="radio">
                        <input
                            type="radio"
                            value='mode2'
                            onChange={this.onModeChange}
                            checked={this.state.analyze_mode === 'mode2'}
                        />
                        <span>Динамический анализ</span>
                    </div>
                </div>

                <div className="analyze__upload-block">
                    <div className="analyze__upload-buttons">
                        <button
                            className='btn'
                            onClick={() => this.refs.fileInput.click()}
                        >
                            Выбрать файл
                        </button>
                        <input
                            ref="fileInput"
                            onChange={this.handleFileUpload}
                            type="file"
                            style={{ display: "none" }}
                        />

                        <button
                            className="btn"
                            onClick={this.onUploadButtonClick}
                            disabled={this.state.fileToUpload === undefined}
                        >
                            Загрузить файл
                        </button>
                    </div>
                    <div className="analyze__upload-filename">
                        {this.state.fileToUpload !== undefined && (
                            <div className="">
                                {this.state.fileToUpload.name}
                            </div>
                        )}
                    </div>
                </div>
            </div>
        );
    }
};

export default AnalysisPage;
