import React from 'react';

import './Pages.scss';


class SearchPage extends React.Component {
    state = {
        input: '',
        fetchedData: undefined,
        search_mode: 'dynamic_search'
    };

    onSearchInputChange = (e) => {
        this.setState({
            input: e.target.value
        });
    };

    onSearchButtonClick = (e) => {
        e.preventDefault();



        this.setState({
            fetchedData: jsonExample
        });
    };

    onModeChange = (e) => {
        console.log(e.target.value);
    }

    render() {
        return (
            <div className="search">
                <div className="search__radio">
                    <div className="radio">
                        <input
                            type="radio"
                            aria-label='Mode 1'
                            value='dynamic_search'
                            onChange={this.onModeChange}
                            defaultChecked={this.state.search_mode === 'dynamic_search'}
                        />
                        <span>Динамический поиск</span>
                    </div>

                    <div className="radio">
                        <input
                            type="radio"
                            aria-label='Mode 1'
                            value='search_ip'
                            onChange={this.onModeChange}
                        />
                        <span>Поиск по IP</span>
                    </div>

                    <div className="radio">
                        <input
                            type="radio"
                            aria-label='Mode 1'
                            value='date'
                            onChange={this.onModeChange}
                        />
                        <span>Поиск по дате в формате `дд/мм/гггг`</span>
                    </div>

                    <div className="radio">
                        <input
                            type="radio"
                            aria-label='Mode 1'
                            value='hash'
                            onChange={this.onModeChange}
                        />
                        <span>Поиск по Хэшу `sha1`</span>
                    </div>
                </div>

                <div className="search__input-block">
                    <input
                        type={'text'}
                        placeholder={'Введите текст для поиска: ...'}
                        className={'search__input'}
                        onChange={this.onSearchInputChange}
                    />
                    <button
                        onClick={this.onSearchButtonClick}
                        className="search__input-btn"
                    >
                        Найти
                    </button>
                </div>

                <div className="search__body">
                    <pre>
                        {this.state.fetchedData && JSON.stringify(this.state.fetchedData, null, 2)}
                    </pre>
                </div>
            </div>
        );
    }
};

export default SearchPage;



const jsonExample = {
    "glossary": {
        "title": "example glossary",
        "GlossDiv": {
            "title": "S",
            "GlossList": {
                "GlossEntry": {
                    "ID": "SGML",
                    "SortAs": "SGML",
                    "GlossTerm": "Standard Generalized Markup Language",
                    "Acronym": "SGML",
                    "Abbrev": "ISO 8879:1986",
                    "GlossDef": {
                        "para": "A meta-markup language, used to create markup languages such as DocBook.",
                        "GlossSeeAlso": ["GML", "XML"]
                    },
                    "GlossSee": "markup"
                }
            }
        }
    }
};
